import React, { createContext, useContext, useEffect, useMemo, useState } from "react";

const AuthContext = createContext({ authorized: false, setAuthorized: () => {} });

export const AuthProvider = ({ children }) => {
  const [authorized, setAuthorized] = useState(false);

  useEffect(() => {
    const stored = localStorage.getItem("tm_authorized");
    if (stored === "true") setAuthorized(true);
  }, []);

  useEffect(() => {
    localStorage.setItem("tm_authorized", authorized ? "true" : "false");
  }, [authorized]);

  const value = useMemo(() => ({ authorized, setAuthorized }), [authorized]);
  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

export const useAuth = () => useContext(AuthContext);
