// src/pages/Blog.js
import React, { useEffect, useState } from "react";
import Section from "../components/UI/Section";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

const Blog = () => {
  const { username, memberName } = useAuth();
  const currentUser = username || memberName || "Member";
  const API = "/api";
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const loadPosts = async () => {
    try {
      setLoading(true);
      const res = await fetch(`${API}/posts`, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to load");
      const data = await res.json();
      setPosts(Array.isArray(data) ? data : []);
    } catch {
      setError("Failed to load posts. Please log in.");
    } finally {
      setLoading(false);
    }
  };
  useEffect(() => { loadPosts(); }, []);

  // Create
  const [newTitle, setNewTitle] = useState("");
  const [newBody, setNewBody] = useState("");
  const addPost = async (e) => {
    e.preventDefault();
    if (!newTitle.trim()) return;
    try {
      const res = await fetch(`${API}/posts`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ title: newTitle.trim(), body: newBody.trim() })
      });
      if (!res.ok) throw new Error("Create failed");
      const post = await res.json();
      setPosts((prev) => [post, ...prev]);
      setNewTitle("");
      setNewBody("");
    } catch {
      setError("Failed to create post. Please log in.");
    }
  };

  // Update
  const [editingId, setEditingId] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editBody, setEditBody] = useState("");
  const startEdit = (p) => { setEditingId(p.id); setEditTitle(p.title); setEditBody(p.body); };
  const cancelEdit = () => { setEditingId(null); setEditTitle(""); setEditBody(""); };
  const saveEdit = async (id) => {
    if (!editTitle.trim()) return;
    try {
      const res = await fetch(`${API}/posts/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ title: editTitle.trim(), body: editBody.trim() })
      });
      if (!res.ok) throw new Error("Update failed");
      const updated = await res.json();
      setPosts((prev) => prev.map((p) => (p.id === id ? updated : p)));
      cancelEdit();
    } catch {
      setError("Failed to update post.");
    }
  };

  // Delete
  const removePost = async (id) => {
    if (!window.confirm("Delete this post?")) return;
    try {
      const res = await fetch(`${API}/posts/${id}`, { method: "DELETE", credentials: "include" });
      if (!res.ok && res.status !== 204) throw new Error("Delete failed");
      setPosts((prev) => prev.filter((p) => p.id !== id));
    } catch {
      setError("Failed to delete post.");
    }
  };

  // Comments
  const [commentDrafts, setCommentDrafts] = useState({});
  const addComment = async (postId) => {
    const text = (commentDrafts[postId] || "").trim();
    if (!text) return;
    try {
      const res = await fetch(`${API}/posts/${postId}/comments`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ body: text })
      });
      if (!res.ok) throw new Error("Comment failed");
      const comment = await res.json();
      setPosts((prev) => prev.map((p) => (p.id === postId ? { ...p, comments: [...(p.comments || []), comment] } : p)));
      setCommentDrafts((d) => ({ ...d, [postId]: "" }));
    } catch {
      setError("Failed to add comment.");
    }
  };
  const removeComment = async (postId, commentId) => {
    try {
      const res = await fetch(`${API}/posts/${postId}/comments/${commentId}`, { method: "DELETE", credentials: "include" });
      if (!res.ok && res.status !== 204) throw new Error("Delete comment failed");
      setPosts((prev) => prev.map((p) => (p.id === postId ? { ...p, comments: (p.comments || []).filter((c) => c.id !== commentId) } : p)));
    } catch {
      setError("Failed to delete comment.");
    }
  };

  // Read (expand/collapse)
  const [openIds, setOpenIds] = useState({});
  const toggleOpen = (id) => setOpenIds((s) => ({ ...s, [id]: !s[id] }));

  const composeEmail = (subject) => {
    const s = encodeURIComponent(subject);
    const b = encodeURIComponent("I'd like to submit a topic/question for the Taylor-Made Blog.\n\nTopic: ");
    window.location.href = `mailto:RegistrywithTaylor@gmail.com?subject=${s}&body=${b}`;
  };

  return (
    <div className="min-h-screen bg-white">
      <Section title="Taylor‑Made Blog" center tightTop compact>
        <div className="max-w-4xl mx-auto text-black/80">
          <div className="flex flex-wrap items-center justify-center gap-3 mb-6">
            <Link to="/welcome" className="px-4 py-2 rounded-lg border-2 border-gold/40 bg-white text-black hover:brightness-95">Back to Portal</Link>
            <Link to="/services" className="px-4 py-2 rounded-lg border-2 border-gold/40 bg-white text-black hover:brightness-95">Explore Services</Link>
            <Link to="/edit-profile" className="px-4 py-2 rounded-lg border-2 border-gold/40 bg-white text-black hover:brightness-95">Edit Profile</Link>
            <button className="btn btn-primary" onClick={() => composeEmail("Question for Taylor-Made")}>Ask Taylor</button>
          </div>
          {error && <p className="mb-4 text-red-600">{error}</p>}
          <p className="mb-6">A private space for the Taylor‑Made community to share tips, questions, and wins.</p>

          {/* Create */}
          <form onSubmit={addPost} className="cc-card mb-8 grid gap-3 text-left">
            <h3 className="font-serif text-xl text-black">Post a New Topic</h3>
            <input
              type="text"
              value={newTitle}
              onChange={(e)=>setNewTitle(e.target.value)}
              placeholder="Topic title"
              className="w-full rounded-lg border-2 border-gold/40 focus:border-gold px-4 py-3 bg-white"
            />
            <textarea
              value={newBody}
              onChange={(e)=>setNewBody(e.target.value)}
              placeholder="Write your post (you can edit later)"
              rows="4"
              className="w-full rounded-lg border-2 border-gold/40 focus:border-gold px-4 py-3 bg-white"
            />
            <div className="flex gap-3">
              <button type="submit" className="btn btn-primary">Publish</button>
              <button type="button" className="px-4 py-3 rounded-lg border-2 border-gold/40 bg-white text-black" onClick={()=>{setNewTitle("");setNewBody("");}}>Clear</button>
            </div>
          </form>

          {/* Read / Update / Delete */}
          {loading ? <p>Loading…</p> : (
          <div className="grid gap-6 md:grid-cols-2">
            {posts.map((p) => {
              const canModify = p.author === currentUser;
              return (
                <div key={p.id} className="cc-card text-left">
                  {editingId === p.id ? (
                  <>
                    <input
                      type="text"
                      value={editTitle}
                      onChange={(e)=>setEditTitle(e.target.value)}
                      className="w-full rounded-lg border-2 border-gold/40 focus:border-gold px-3 py-2 bg-white mb-2"
                    />
                    <textarea
                      value={editBody}
                      onChange={(e)=>setEditBody(e.target.value)}
                      rows={4}
                      className="w-full rounded-lg border-2 border-gold/40 focus:border-gold px-3 py-2 bg-white"
                    />
                    <div className="mt-3 flex gap-3">
                      <button className="btn btn-primary" onClick={()=>saveEdit(p.id)}>Save</button>
                      <button className="px-4 py-2 rounded-lg border-2 border-gold/40 bg-white text-black" onClick={cancelEdit}>Cancel</button>
                    </div>
                  </>
                  ) : (
                  <>
                    <div className="flex items-baseline justify-between gap-3">
                      <h3 className="font-serif text-xl text-black mb-2">{p.title}</h3>
                      <span className="text-xs text-black/60">by {p.author}</span>
                    </div>
                    <p className={`text-black/80 ${openIds[p.id] ? '' : 'line-clamp-3'}`}>{p.body}</p>
                    <div className="mt-4 flex flex-wrap gap-3">
                      <button className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black hover:brightness-95" onClick={()=>toggleOpen(p.id)}>
                        {openIds[p.id] ? 'Collapse' : 'Read more'}
                      </button>
                      {canModify && (
                        <>
                          <button className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black hover:brightness-95" onClick={()=>startEdit(p)}>
                            Edit
                          </button>
                          <button className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black hover:brightness-95" onClick={()=>removePost(p.id)}>
                            Delete
                          </button>
                        </>
                      )}
                      <Link to="/services" className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black hover:brightness-95">Related Services</Link>
                    </div>

                    {/* Comments */}
                    <div className="mt-5 pt-5 border-t border-gold/40">
                      <h4 className="font-serif text-lg text-black mb-3">Comments</h4>
                      <div className="grid gap-3">
                        {(p.comments || []).map((c) => (
                          <div key={c.id} className="rounded-xl border border-gold/30 bg-white px-3 py-2 flex items-start justify-between gap-3">
                            <div>
                              <div className="text-sm text-black/60">{c.author}</div>
                              <div className="text-black/80">{c.body}</div>
                            </div>
                            {c.author === currentUser && (
                              <button
                                className="px-2 py-1 rounded border border-gold/40 text-black bg-white text-xs"
                                onClick={() => removeComment(p.id, c.id)}
                              >
                                Delete
                              </button>
                            )}
                          </div>
                        ))}
                      </div>
                      <div className="mt-3 flex gap-2">
                        <input
                          type="text"
                          value={commentDrafts[p.id] || ''}
                          onChange={(e)=>setCommentDrafts((d)=>({ ...d, [p.id]: e.target.value }))}
                          placeholder="Add a comment"
                          className="flex-1 rounded-lg border-2 border-gold/40 focus:border-gold px-3 py-2 bg-white"
                        />
                        <button className="btn btn-primary" onClick={()=>addComment(p.id)}>Post</button>
                      </div>
                    </div>
                  </>
                  )}
                </div>
              );
            })}
          </div>
          )}
          <div className="mt-8 text-center">
            <button className="btn btn-primary" onClick={() => composeEmail("Blog Topic Submission")}>Submit a Topic</button>
          </div>
        </div>
      </Section>
    </div>
  );
};

export default Blog;
