// src/pages/UsersAdmin.js
import React, { useEffect, useMemo, useState } from 'react';
import Section from '../components/UI/Section';

const UsersAdmin = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [savingId, setSavingId] = useState('');
  const [showAll, setShowAll] = useState(false);

  const load = async () => {
    setError('');
    try {
      setLoading(true);
      const res = await fetch('/api/users', { credentials: 'include' });
      const data = await res.json();
      setUsers(Array.isArray(data) ? data : []);
    } catch (e) {
      setError('Failed to load users');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); }, []);

  const requested = useMemo(() => users.filter(u => (u.status || '').toLowerCase() === 'requested'), [users]);
  const active = useMemo(() => users.filter(u => (u.status || '').toLowerCase() === 'active'), [users]);

  const updateUser = async (id, patch) => {
    setSavingId(id);
    setError('');
    try {
      const res = await fetch(`/api/users/${id}`, { method: 'PUT', headers: { 'Content-Type': 'application/json' }, credentials: 'include', body: JSON.stringify(patch) });
      if (!res.ok) throw new Error('Update failed');
      await load();
    } catch (e) {
      setError(e.message || 'Failed to update user');
    } finally {
      setSavingId('');
    }
  };

  const approve = async (u, role = 'member') => {
    await updateUser(u.id, { status: 'active', role });
  };

  const reject = async (u) => {
    if (!window.confirm(`Reject access request for ${u.memberName || u.email || u.username || 'user'}?`)) return;
    await updateUser(u.id, { status: 'rejected' });
  };

  const updateRoleLocal = (u, role) => {
    setUsers(prev => prev.map(x => x.id === u.id ? { ...x, role } : x));
  };

  return (
    <div className="min-h-screen bg-white">
      <Section title="Manage Users" center tightTop compact>
        {error && <p className="text-red-600 mb-4">{error}</p>}

        <div className="cc-card text-left mb-8">
          <div className="flex items-center justify-between gap-4">
            <h3 className="font-serif text-xl text-black">Requested Access</h3>
            <button className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black" onClick={load}>
              Refresh
            </button>
          </div>
          {loading ? (
            <p className="mt-4">Loading…</p>
          ) : requested.length === 0 ? (
            <p className="mt-4 text-black/70">No pending requests.</p>
          ) : (
            <div className="mt-4 grid gap-4">
              {requested.map((u) => (
                <div key={u.id} className="rounded-xl border-2 border-gold/40 bg-white p-4">
                  <div className="flex flex-wrap items-center justify-between gap-3">
                    <div>
                      <div className="text-black font-medium">{u.memberName || `${u.parent1 || ''}${u.parent2 ? ' & ' + u.parent2 : ''}` || u.username || u.email}</div>
                      <div className="text-sm text-black/70">Email: {u.email || '—'}</div>
                      <div className="text-sm text-black/70">Due: {u.originalDueDate || u.dueDate || '—'}{u.gender ? ` • ${u.gender}` : ''}</div>
                    </div>
                    <div className="flex flex-wrap gap-2">
                      <button disabled={savingId===u.id} className="btn btn-primary" onClick={() => approve(u, 'member')}>
                        {savingId===u.id ? 'Saving…' : 'Approve as Member'}
                      </button>
                      <button disabled={savingId===u.id} className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black" onClick={() => approve(u, 'admin')}>
                        {savingId===u.id ? 'Saving…' : 'Approve as Admin'}
                      </button>
                      <button disabled={savingId===u.id} className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black" onClick={() => reject(u)}>
                        Reject
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="cc-card text-left">
          <div className="flex items-center justify-between gap-4">
            <h3 className="font-serif text-xl text-black">Users</h3>
            <div className="flex items-center gap-3">
              <label className="text-sm text-black/70 flex items-center gap-2">
                <input type="checkbox" checked={showAll} onChange={(e)=>setShowAll(e.target.checked)} />
                Show all users
              </label>
              <button className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black" onClick={load}>Refresh</button>
            </div>
          </div>
          {loading ? (
            <p className="mt-4">Loading…</p>
          ) : (
            <div className="mt-4 grid gap-3">
              {(showAll ? users : active).map((u) => (
                <div key={u.id} className="rounded-xl border-2 border-gold/40 bg-white p-4">
                  <div className="flex flex-wrap items-center justify-between gap-3">
                    <div>
                      <div className="text-black font-medium">{u.memberName || u.username || u.email}</div>
                      <div className="text-sm text-black/70">Status: {u.status || '—'} • Email: {u.email || '—'}</div>
                    </div>
                    <div className="flex items-center gap-2">
                      <select
                        className="rounded-lg border-2 border-gold/40 px-3 py-2 bg-white"
                        value={u.role || 'member'}
                        onChange={(e)=>updateRoleLocal(u, e.target.value)}
                      >
                        <option value="member">Member</option>
                        <option value="admin">Admin</option>
                      </select>
                      <button disabled={savingId===u.id} className="btn btn-primary" onClick={()=>updateUser(u.id, { role: u.role || 'member' })}>
                        {savingId===u.id ? 'Saving…' : 'Save'}
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </Section>
    </div>
  );
};

export default UsersAdmin;
