import { useState } from "react";
import ChecklistItem from "./ChecklistItem";

export default function BabyChecklist() {
  const [items, setItems] = useState([
    { label: "Set up crib ðŸ›ï¸", checked: false },
    { label: "Buy baby clothes ðŸ‘•", checked: false },
    { label: "Pack hospital bag ðŸŽ’", checked: false },
    { label: "Install car seat ðŸš—", checked: false },
    { label: "Schedule pediatrician visit ðŸ©º", checked: false },
  ]);

  const toggleItem = (index) => {
    setItems((prev) =>
      prev.map((item, i) =>
        i === index ? { ...item, checked: !item.checked } : item
      )
    );
  };

  return (
    <div className="bg-pastelYellow/20 p-6 rounded-2xl shadow-md">
      <h2 className="text-xl font-serif font-semibold text-babyBlue mb-4">
        Baby Prep Checklist
      </h2>
      {items.map((item, index) => (
        <ChecklistItem
          key={index}
          label={item.label}
          checked={item.checked}
          onToggle={() => toggleItem(index)}
        />
      ))}
    </div>
  );
}
