// src/pages/Blog.js
import React, { useEffect, useState } from "react";
import Section from "../components/UI/Section";
import { Link } from "react-router-dom";
import defaults from "../data/blogPosts.json";

const Blog = () => {
  const STORAGE_KEY = "tm_blog_posts";
  const genId = () => `${Date.now()}_${Math.random().toString(36).slice(2, 8)}`;
  const normalize = (arr) => arr.map((t) => ({ id: t.id || genId(), title: t.title, body: t.excerpt || t.body || "" }));

  const [posts, setPosts] = useState(() => {
    try {
      const saved = localStorage.getItem(STORAGE_KEY);
      return saved ? JSON.parse(saved) : normalize(defaults);
    } catch {
      return normalize(defaults);
    }
  });
  useEffect(() => {
    try { localStorage.setItem(STORAGE_KEY, JSON.stringify(posts)); } catch {}
  }, [posts]);

  // Create
  const [newTitle, setNewTitle] = useState("");
  const [newBody, setNewBody] = useState("");
  const addPost = (e) => {
    e.preventDefault();
    if (!newTitle.trim()) return;
    setPosts([{ id: genId(), title: newTitle.trim(), body: newBody.trim(), createdAt: Date.now() }, ...posts]);
    setNewTitle("");
    setNewBody("");
  };

  // Update
  const [editingId, setEditingId] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editBody, setEditBody] = useState("");
  const startEdit = (p) => { setEditingId(p.id); setEditTitle(p.title); setEditBody(p.body); };
  const cancelEdit = () => { setEditingId(null); setEditTitle(""); setEditBody(""); };
  const saveEdit = (id) => {
    if (!editTitle.trim()) return;
    setPosts((prev) => prev.map((p) => (p.id === id ? { ...p, title: editTitle.trim(), body: editBody.trim() } : p)));
    cancelEdit();
  };

  // Delete
  const removePost = (id) => {
    if (!confirm("Delete this post?")) return;
    setPosts((prev) => prev.filter((p) => p.id !== id));
  };

  // Read (expand/collapse)
  const [openIds, setOpenIds] = useState({});
  const toggleOpen = (id) => setOpenIds((s) => ({ ...s, [id]: !s[id] }));

  const composeEmail = (subject) => {
    const s = encodeURIComponent(subject);
    const b = encodeURIComponent("I'd like to submit a topic/question for the Taylor-Made Blog.\n\nTopic: ");
    window.location.href = `mailto:RegistryTaylor@gmail.com?subject=${s}&body=${b}`;
  };

  return (
    <div className="min-h-screen bg-white">
      <Section title="Taylor‑Made Blog" center tightTop compact>
        <div className="max-w-4xl mx-auto text-black/80">
          <div className="flex flex-wrap items-center justify-center gap-3 mb-6">
            <Link to="/welcome" className="px-4 py-2 rounded-lg border-2 border-gold/40 bg-white text-black hover:brightness-95">Back to Portal</Link>
            <Link to="/services" className="px-4 py-2 rounded-lg border-2 border-gold/40 bg-white text-black hover:brightness-95">Explore Services</Link>
            <Link to="/edit-profile" className="px-4 py-2 rounded-lg border-2 border-gold/40 bg-white text-black hover:brightness-95">Edit Profile</Link>
            <button className="btn btn-primary" onClick={() => composeEmail("Question for Taylor-Made")}>Ask Taylor</button>
          </div>
          <p className="mb-6">A private space for the Taylor‑Made community to share tips, questions, and wins.</p>

          {/* Create */}
          <form onSubmit={addPost} className="cc-card mb-8 grid gap-3 text-left">
            <h3 className="font-serif text-xl text-black">Post a New Topic</h3>
            <input
              type="text"
              value={newTitle}
              onChange={(e)=>setNewTitle(e.target.value)}
              placeholder="Topic title"
              className="w-full rounded-lg border-2 border-gold/40 focus:border-gold px-4 py-3 bg-white"
            />
            <textarea
              value={newBody}
              onChange={(e)=>setNewBody(e.target.value)}
              placeholder="Write your post (you can edit later)"
              rows="4"
              className="w-full rounded-lg border-2 border-gold/40 focus:border-gold px-4 py-3 bg-white"
            />
            <div className="flex gap-3">
              <button type="submit" className="btn btn-primary">Publish</button>
              <button type="button" className="px-4 py-3 rounded-lg border-2 border-gold/40 bg-white text-black" onClick={()=>{setNewTitle("");setNewBody("");}}>Clear</button>
            </div>
          </form>

          {/* Read / Update / Delete */}
          <div className="grid gap-6 md:grid-cols-2">
            {posts.map((p) => (
              <div key={p.id} className="cc-card text-left">
                {editingId === p.id ? (
                  <>
                    <input
                      type="text"
                      value={editTitle}
                      onChange={(e)=>setEditTitle(e.target.value)}
                      className="w-full rounded-lg border-2 border-gold/40 focus:border-gold px-3 py-2 bg-white mb-2"
                    />
                    <textarea
                      value={editBody}
                      onChange={(e)=>setEditBody(e.target.value)}
                      rows={4}
                      className="w-full rounded-lg border-2 border-gold/40 focus:border-gold px-3 py-2 bg-white"
                    />
                    <div className="mt-3 flex gap-3">
                      <button className="btn btn-primary" onClick={()=>saveEdit(p.id)}>Save</button>
                      <button className="px-4 py-2 rounded-lg border-2 border-gold/40 bg-white text-black" onClick={cancelEdit}>Cancel</button>
                    </div>
                  </>
                ) : (
                  <>
                    <h3 className="font-serif text-xl text-black mb-2">{p.title}</h3>
                    <p className={`text-black/80 ${openIds[p.id] ? '' : 'line-clamp-3'}`}>{p.body}</p>
                    <div className="mt-4 flex flex-wrap gap-3">
                      <button className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black hover:brightness-95" onClick={()=>toggleOpen(p.id)}>
                        {openIds[p.id] ? 'Collapse' : 'Read more'}
                      </button>
                      <button className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black hover:brightness-95" onClick={()=>startEdit(p)}>
                        Edit
                      </button>
                      <button className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black hover:brightness-95" onClick={()=>removePost(p.id)}>
                        Delete
                      </button>
                      <Link to="/services" className="px-3 py-2 rounded border-2 border-gold/40 bg-white text-black hover:brightness-95">Related Services</Link>
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
          <div className="mt-8 text-center">
            <button className="btn btn-primary" onClick={() => composeEmail("Blog Topic Submission")}>Submit a Topic</button>
          </div>
        </div>
      </Section>
    </div>
  );
};

export default Blog;
