import React from "react";
import { Link } from "react-router-dom";
import Section from "../components/UI/Section";
import SectionDivider from "../components/UI/SectionDivider";
import { motion } from "framer-motion";
import DueDateCountdown from "../components/DueDateCountdown";
import BabyChecklist from "../components/BabyChecklist";

const Home = () => {
  return (
    <div className="min-h-screen bg-white">
      {/* Hero Section */}
      <Section center tightTop compact>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="relative overflow-hidden rounded-[2.5rem] border border-gold/40 bg-gradient-to-br from-babyPink/30 via-white to-babyBlue/30 px-6 py-20 text-center shadow-[0_25px_60px_-35px_rgba(212,175,55,0.4)]"
        >
          <motion.h1
            initial={{ opacity: 0, y: 24, scale: 0.98 }}
            animate={{ opacity: 1, y: 0, scale: [0.98, 1.03, 1] }}
            transition={{ duration: 0.55, ease: "easeOut" }}
            className="mb-6 text-center font-serif leading-tight text-babyBlue text-4xl sm:text-5xl tracking-tight"
          >
            <span className="font-cursive text-6xl sm:text-[4.75rem] inline-block mr-3 text-babyPink">
              Taylor-Made
            </span>
            Baby Planning
          </motion.h1>

          <p className="text-lg md:text-xl text-black/70 max-w-2xl mx-auto">
            A calm, confident path to welcoming your little one. Personalized
            registries, nursery design, shower planning, and ongoing support‚Äî
            tailored to your family.
          </p>

          <motion.img
            src="/assets/hero-baby.png"
            alt="Baby Planning Illustration"
            className="mx-auto mt-6 max-w-md drop-shadow-lg animate-float"
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, ease: "easeOut", delay: 0.3 }}
          />

          <div className="mt-8 flex flex-wrap justify-center gap-4">
            <Link
              to="/services"
              className="px-8 py-3 rounded-full bg-gradient-to-r from-babyPink to-babyBlue text-white font-semibold shadow-md hover:opacity-90 transition"
            >
              Explore Services
            </Link>
            <Link
              to="/contact"
              className="px-8 py-3 rounded-full border border-gold/40 bg-white text-black font-medium tracking-wide transition hover:border-gold hover:shadow-lg"
            >
              Book a Consultation
            </Link>
          </div>

          <div className="mt-12 grid gap-6 text-left text-black/80 md:grid-cols-3">
            {[
              { icon: "üçº", label: "Invite-Only", detail: "A discreet client list curated by referral." },
              { icon: "üß∏", label: "Concierge Care", detail: "Taylor personally oversees every decision." },
              { icon: "üë∂", label: "Confidential Always", detail: "NDA-backed planning and event management." },
            ].map(({ icon, label, detail }, index) => (
              <motion.div
                key={label}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: 0.2 + index * 0.15 }}
                className="rounded-2xl border border-gold/30 bg-white/80 px-5 py-4 shadow-sm transition transform hover:scale-105 hover:shadow-lg hover:bg-babyBlue/10"
              >
                <div className="text-3xl">{icon}</div>
                <p className="mt-3 font-serif text-lg text-black">{label}</p>
                <p className="text-sm text-black/70">{detail}</p>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </Section>

      {/* Countdown + Checklist */}
      <Section compact>
        <div className="grid gap-6 md:grid-cols-2 max-w-4xl mx-auto">
          <DueDateCountdown dueDate="2025-12-01" />
          <BabyChecklist />
        </div>
      </Section>

      <SectionDivider className="my-6" />

      {/* Introduction Section */}
      <Section title="Introduction" tightTop compact className="bg-babyBlue/10 rounded-3xl">
        <div className="px-6 py-4 max-w-3xl mx-auto text-black/80 space-y-6">
          <div className="border-l-4 border-babyPink pl-3">
            <h3 className="font-serif text-2xl text-babyPink mb-2">The Problem</h3>
            <p className="leading-relaxed text-lg">Preparing for a baby is supposed to feel exciting ‚Äî but too often it‚Äôs overwhelming...</p>
          </div>
          <div className="border-l-4 border-babyBlue pl-3">
            <h3 className="font-serif text-2xl text-babyBlue mb-2">The Solution</h3>
            <p className="leading-relaxed text-lg">Taylor-Made Baby Planning takes the pressure off your shoulders...</p>
          </div>
          <div className="border-l-4 border-gold pl-3">
            <h3 className="font-serif text-2xl text-gold mb-2">The Benefit</h3>
            <p className="leading-relaxed text-lg">With my Taylor-Made approach, you‚Äôll feel calm, confident, and fully prepared...</p>
          </div>
        </div>
      </Section>

      <SectionDivider className="my-6" />

      {/* Services Section */}
      <Section title="Services Snapshot ‚Äî Taylor-Made for You" tightTop compact className="bg-babyPink/10 rounded-3xl">
        <div className="px-6 py-4">
          <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
            {[
              { title: "Registry & Gear Concierge", desc: "Every list curated, managed, and delivered...", icon: "üóíÔ∏è" },
              { title: "Personal Shopping", desc: "In-home or virtual consultations...", icon: "üõçÔ∏è" },
              { title: "Nursery & Home Design", desc: "Furniture, safety, and styling planned...", icon: "üõèÔ∏è" },
              { title: "Events & Celebrations", desc: "Baby showers, sip-and-sees...", icon: "ü•Ç" },
              { title: "Family Integration", desc: "Support for siblings, pets, and family dynamics...", icon: "üë®‚Äçüë©‚Äçüëß‚Äçüë¶" },
              { title: "Ongoing Concierge", desc: "Fourth-trimester coaching...", icon: "‚è≥" },
            ].map((service, index) => (
              <motion.div
                whileHover={{ scale: 1.05, y: -4 }}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.4, delay: index * 0.15 }}
                key={service.title}
                className="rounded-3xl border border-gold/40 bg-white/80 px-6 py-7 shadow-sm backdrop-blur"
              >
                <div className="text-4xl">{service.icon}</div>
                <h3 className="mt-4 font-serif text-2xl text-black">{service.title}</h3>
                <p className="mt-2 text-sm text-black/70 leading-relaxed">{service.desc}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </Section>

      <SectionDivider className="my-6" />

      {/* Testimonials */}
      <Section title="Testimonials" center compact className="bg-babyBlue/10 rounded-3xl">
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8 }}
          className="px-6 py-6 max-w-2xl mx-auto space-y-6 text-black/80"
        >
          <blockquote className="italic border-l-4 border-babyPink pl-4">‚ÄúTaylor made our registry feel effortless ‚Äî no stress, no second-guessing.‚Äù ‚Äî Jenna M.</blockquote>
          <blockquote className="italic border-l-4 border-babyBlue pl-4">‚ÄúShe helped us choose a stroller that truly works for our lifestyle. Game-changer!‚Äù ‚Äî Rachel & Matt K.</blockquote>
        </motion.div>
      </Section>

      <SectionDivider className="my-6" />

      {/* Process */}
      <Section title="How Taylor-Made Baby Planning Works" tightTop compact className="bg-babyPink/10 rounded-3xl">
        <div className="px-6 py-6 max-w-5xl mx-auto text-black/80">
          <ol className="grid gap-6 md:grid-cols-2 xl:grid-cols-3">
            {[
              { step: "1. Private Invite", body: "Membership is invite-only. Choose the package that fits best: Essentials, Signature, or Bespoke." },
              { step: "2. Confidential Agreement", body: "Every member signs an NDA so all details remain private." },
              { step: "3. Concierge Consultation", body: "We begin with a deep-dive consultation to understand your style, lifestyle, and priorities." },
              { step: "4. Curated Planning", body: "Taylor handles everything ‚Äî registry, nursery, family prep, events. You simply approve selections." },
              { step: "5. Add-On Flexibility", body: "Need more? Add shower planning, extended styling, or postpartum support at member rates." },
              { step: "6. Ongoing Access", body: "Stay connected through the Taylor-Made Blog and Mom Mentor Circle." },
              { step: "7. Always Discreet", body: "From start to finish, the experience is confidential, stress-free, and fully tailored." },
            ].map(({ step, body }, index) => (
              <motion.li
                key={step}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.4, delay: index * 0.15 }}
                className="relative overflow-hidden rounded-3xl border border-gold/30 bg-white/85 px-6 py-6 shadow-sm"
              >
                <div className="flex items-start gap-4">
                  <span className="flex h-12 w-12 shrink-0 items-center justify-center rounded-full border border-gold bg-white font-serif text-lg text-gold">{step.split(".")[0]}</span>
                  <div>
                    <h3 className="font-serif text-xl text-black">{step.split(". ")[1]}</h3>
                    <p className="mt-2 text-sm leading-relaxed text-black/70">{body}</p>
                  </div>
                </div>
              </motion.li>
            ))}
          </ol>
        </div>
      </Section>

      <SectionDivider className="my-6" />

      {/* Membership */}
      <Section title="Taylor-Made Membership Perks" tightTop compact className="bg-babyBlue/10 rounded-3xl">
        <div className="px-6 py-6 max-w-4xl mx-auto text-black/80 space-y-6">
          <p className="text-lg text-black/70">Every package unlocks access to an exclusive community experience...</p>
          <div className="grid gap-6 md:grid-cols-2">
            {[
              { title: "Taylor-Made Blog", icon: "üìí", points: ["Private resource hub with curated comparisons.", "Insider updates on product launches.", "Seasonal checklists for hospital, holidays, and toddler transitions."] },
              { title: "Taylor-Made Mentors", icon: "ü§ù", points: ["Invitation-only circle of mothers offering lived wisdom.", "Personal matching with a mentor.", "Opportunities to mentor others."] },
            ].map((perk, index) => (
              <motion.div
                key={perk.title}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.4, delay: index * 0.2 }}
                className="rounded-3xl border border-gold/40 bg-white/80 px-6 py-7 shadow-sm"
              >
                <div className="text-4xl">{perk.icon}</div>
                <h3 className="mt-4 font-serif text-2xl text-black">{perk.title}</h3>
                <ul className="mt-3 space-y-2 text-sm leading-relaxed text-black/70">
                  {perk.points.map((point, i) => (
                    <li key={i} className="flex gap-2">
                      <span className="text-gold">‚óÜ</span>
                      <span>{point}</span>
                    </li>
                  ))}
                </ul>
              </motion.div>
            ))}
          </div>
        </div>
      </Section>

      <SectionDivider className="my-6" />

      {/* About */}
      <Section title="About Me" tightTop compact className="bg-babyPink/10 rounded-3xl">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="px-6 py-4 max-w-3xl mx-auto text-black/80"
        >
          <p className="max-w-2xl">Hi, I‚Äôm Taylor! Think of me as your go-to guide (and maybe your new best friend) for all things baby prep...</p>
          <p className="mt-6 max-w-2xl">Now, I bring that experience to you in a warm, personalized way that takes away the stress and leaves you excited, prepared, and confident.</p>
        </motion.div>
      </Section>
    </div>
  );
};

export default Home;
