import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { apiFetch } from "../utils/api";

const NavBar = () => {
  const navigate = useNavigate();
  const {
    authorized,
    memberName,
    role,
    setAuthorized,
    setMemberName,
    setDueDate,
    setMonthsPregnant,
    setOriginalDueDate,
    setRole,
    setUserId,
    setUsername,
    setBabyName,
    setGender,
    setAuthCode,
    setPasswordHash,
  } = useAuth();

  const logout = async () => {
    try {
      await apiFetch('/api/logout', { method: 'POST' });
    } catch {}
    setAuthorized(false);
    setRole("");
    setUserId("");
    setUsername("");
    setMemberName("");
    setBabyName("");
    setGender("");
    setAuthCode("");
    setPasswordHash("");
    setDueDate("");
    setMonthsPregnant(null);
    setOriginalDueDate("");
    navigate("/");
  };
  if (!authorized) return null;
  return (
    <nav
      style={{
        position: "sticky",
        top: 0,
        zIndex: 30,
        background: "#fff",
        borderBottom: "1px solid #D4AF37",
        padding: "0.5rem 1rem",
      }}
    >
      <div style={{ display: "flex", justifyContent: "flex-end", gap: "1rem", alignItems: "center" }}>
        <Link to="/blog" style={{ color: "#000", textDecoration: "none" }}>
          Blog
        </Link>
        {role === 'admin' && (
          <>
            <Link to="/admin/users" style={{ color: "#000", textDecoration: "none" }}>
              Users
            </Link>
            <Link to="/admin/invites" style={{ color: "#000", textDecoration: "none" }}>
              Invites
            </Link>
          </>
        )}
        <Link to="/edit-profile" style={{ color: "#000", textDecoration: "none" }}>
          Edit Profile
        </Link>
        <span style={{ color: "#000" }}>
          Welcome, <span className="font-cursive">{memberName}</span>
        </span>
        <button
          onClick={logout}
          style={{
            color: "#000",
            background: "transparent",
            border: "1px solid #D4AF37",
            padding: "0.25rem 0.75rem",
            borderRadius: "9999px",
            cursor: "pointer",
          }}
        >
          Log out
        </button>
      </div>
    </nav>
  );
};

export default NavBar;
