import React from "react";
import { Link } from "react-router-dom";
import Section from "../components/UI/Section";
import SectionDivider from "../components/UI/SectionDivider";
import { motion } from "framer-motion";
import DueDateCountdown from "../components/DueDateCountdown";
import BabyChecklist from "../components/BabyChecklist";

const Home = () => {
  return (
    <div className="min-h-screen bg-white">
      {/* Hero Section */}
      <Section center tightTop compact>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="relative overflow-hidden rounded-3xl border border-babyBlue/20 bg-gradient-to-br from-babyPink/30 via-white to-babyBlue/30 px-4 py-12 text-center shadow-md"
        >
          <motion.h1
            initial={{ opacity: 0, y: 24, scale: 0.98 }}
            animate={{ opacity: 1, y: 0, scale: [0.98, 1.03, 1] }}
            transition={{ duration: 0.55, ease: "easeOut" }}
