// src/utils/api.js
const BASE = process.env.REACT_APP_API_BASE || '';

export const apiUrl = (path) => `${BASE}${path}`;

export const apiFetch = (path, options = {}) => {
  const { credentials, ...rest } = options || {};
  return fetch(apiUrl(path), {
    credentials: credentials || 'include',
    ...rest,
  });
};
